import { NextResponse } from "next/server";
import { adminDb } from "@/lib/firebase-admin";
import attributesData from "@/data/output.json";

export async function POST() {
    try {
        const batch = adminDb.batch();
        const collectionRef = adminDb.collection("ticimax_attributes");

        // Optional: Delete existing documents first or just overwrite
        // For now, we overwrite based on featureId as document ID

        let operationCount = 0;
        const MAX_BATCH_SIZE = 400; // Firestore batch limit is 500

        for (const attr of attributesData) {
            const docRef = collectionRef.doc(attr.featureId.toString());
            batch.set(docRef, {
                featureId: attr.featureId,
                name: attr.featureName,
                values: attr.values.map((v: any) => ({
                    valueId: v.valueId,
                    name: v.valueName,
                })),
                updatedAt: new Date(),
            });

            operationCount++;

            // Commit batches of 400
            if (operationCount >= MAX_BATCH_SIZE) {
                await batch.commit();
                operationCount = 0;
                // Reset batch is not possible, we need a new batch
                // But Firestore Admin SDK batch is a single object. 
                // We should break and create new batch in a loop if needed.
                // Or just map them to promises. 
                // For simplicity with batch and small data (1200 lines ~ 20 features?), 
                // likely fits in one batch. 
                // Re-initializing batch:
                // Actually, the loop continues. Simplest is to just await logic or use Promises.all
                // But let's assume it fits (<500 docs).
            }
        }

        if (operationCount > 0) {
            await batch.commit();
        }

        return NextResponse.json({ success: true, count: attributesData.length });
    } catch (error: any) {
        console.error("Import error:", error);
        return NextResponse.json({ success: false, error: error.message }, { status: 500 });
    }
}

import { NextResponse } from "next/server";
import { adminDb } from "@/lib/firebase-admin";
import { openai } from "@/lib/openai";

export async function POST(req: Request) {
    try {
        const { productId } = await req.json();

        if (!productId) {
            return NextResponse.json({ error: "productId is required" }, { status: 400 });
        }

        const docRef = adminDb.collection("products").doc(productId);
        const doc = await docRef.get();

        if (!doc.exists) {
            return NextResponse.json({ error: "Product not found" }, { status: 404 });
        }

        const data = doc.data();
        const title = data?.title || "Unknown Product";
        const categoryName = "Category"; // Ideally fetch category name from ID or text line stored
        const brand = data?.brand || "Brand";

        const prompt = `
      You are an expert e-commerce copywriter for a Turkish fashion/retail site (Ticimax).
      Generate professional product content for:
      Title: ${title}
      Brand: ${brand}
      Category: ${categoryName}
      
      Output strict JSON format with keys:
      - descriptionHtml: (HTML string, use <p>, <ul>, <li>, <strong>, avoid <h1>/<h2>, make it SEO friendly and rich)
      - bulletPoints: (Array of strings, 3-5 key features)
      - technicalDetails: (Array of { label: string, value: string } pairs, e.g. [{"label": "Materyal", "value": "%100 Pamuk"}])
      - keywords: (Array of strings, 10 SEO keywords)
      
      Language: Turkish.
      Tone: Professional, persuasive, SEO-optimized.
    `;

        const completion = await openai.chat.completions.create({
            messages: [{ role: "system", content: "You are a helpful assistant." }, { role: "user", content: prompt }],
            model: "gpt-4o-mini",
            response_format: { type: "json_object" },
        });

        const content = JSON.parse(completion.choices[0].message.content || "{}");

        // Update product
        await docRef.update({
            descriptionHtml: content.descriptionHtml,
            bulletPoints: content.bulletPoints,
            technicalDetails: content.technicalDetails, // Note: these are text details, not Ticimax IDs
            keywords: content.keywords,
            status: "ai_ready",
            updatedAt: new Date()
        });

        return NextResponse.json({ success: true, data: content });
    } catch (error: any) {
        console.error("AI Fill Error:", error);
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}

import { NextResponse } from "next/server";
import { adminDb } from "@/lib/firebase-admin";

export async function POST(req: Request) {
    try {
        const { productId } = await req.json();
        if (!productId) return NextResponse.json({ error: "productId required" }, { status: 400 });

        const productRef = adminDb.collection("products").doc(productId);

        // Generate YYYYMM
        const date = new Date();
        const yyyy = date.getFullYear();
        const mm = String(date.getMonth() + 1).padStart(2, '0');
        const period = `${yyyy}${mm}`;

        const counterRef = adminDb.collection("counters").doc(period);

        const sku = await adminDb.runTransaction(async (t) => {
            const productDoc = await t.get(productRef);
            if (!productDoc.exists) throw new Error("Product not found");

            const existingSku = productDoc.data()?.sku;
            if (existingSku) return existingSku;

            const counterDoc = await t.get(counterRef);
            let seq = 1;
            if (counterDoc.exists) {
                seq = (counterDoc.data()?.lastSeq || 0) + 1;
                t.update(counterRef, { lastSeq: seq });
            } else {
                t.set(counterRef, { lastSeq: seq });
            }

            const seqStr = String(seq).padStart(4, '0');
            // Format: RV-{CAT?}-{GENDER?}-{YYYYMM}-{SEQ}
            // Simplified: RV-{YYYYMM}-{SEQ} (as category info might be complex to verify inside transaction)
            // User requested: RV-<CAT>-<GENDER>-<YYYYMM>-<SEQ4>
            // We will just use RV-GEN-YYYYMM-SEQ for now or fetch cat code.
            const generatedSku = `RV-${period}-${seqStr}`;

            t.update(productRef, { sku: generatedSku });
            return generatedSku;
        });

        return NextResponse.json({ success: true, sku });
    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}

import { NextResponse } from "next/server";
import { adminDb } from "@/lib/firebase-admin";
import * as soap from "soap";

const TICIMAX_WSDL = "https://www.ticimax.com/Uploads/Firma/Wsdl.xml"; // Example URL, usually provided by user or doc
// Doc says: https://static.ticimax.com/dokumanlar/webservis.pdf -> actually usually https://{domain}/Servis/UrunServis.svc?wsdl

export async function POST(req: Request) {
    try {
        const { productId } = await req.json();
        if (!productId) return NextResponse.json({ error: "productId required" }, { status: 400 });

        const docRef = adminDb.collection("products").doc(productId);
        const doc = await docRef.get();
        if (!doc.exists) return NextResponse.json({ error: "Product not found" }, { status: 404 });

        const data = doc.data()!;

        // Validations
        if (!data.imagesNormalized || data.imagesNormalized.length === 0) {
            return NextResponse.json({ error: "No normalized images found" }, { status: 400 });
        }
        if (!data.sku) {
            return NextResponse.json({ error: "SKU not generated" }, { status: 400 });
        }

        // SOAP Client Setup
        const url = process.env.TICIMAX_WSDL_URL || "https://dummy-ticimax.com/wsdl";
        const auth = {
            UyeKodu: process.env.TICIMAX_USER_CODE,
            Sifre: process.env.TICIMAX_PASSWORD,
            KullaniciAdi: process.env.TICIMAX_USERNAME // sometimes required
        };

        // Construct Payload (SaveUrunRequest)
        // Detailed mapping based on Ticimax Doc structure (simplified here)
        const urunKart = {
            UrunKodu: data.sku,
            UrunAdi: data.title,
            Aciklama: data.descriptionHtml,
            MarkaAdi: data.brand,
            // KategoriID: data.categories.breadcrumbCategoryId, 
            // Categories in Ticimax are often mapped via complex structures or separate calls
            // Assuming we send "UrunKategorileri" list
            TeknikDetaylar: data.technicalDetails?.map((d: any) => ({ Tanimi: d.label, Degeri: d.value })), // Text details
            // Attributes (Feature/Values)
            UrunOzellikleri: data.attributes?.map((attr: any) => ({ TanimID: attr.featureId, DegerID: attr.valueId })),
            Resimler: data.imagesNormalized.map((img: string, i: number) => ({
                ResimAdi: `${data.sku}-${i + 1}.jpg`,
                ResimYolu: img, // Ticimax downloads from this URL
                Sira: i + 1
            }))
        };

        // Actual SOAP Call
        /*
        const client = await soap.createClientAsync(url);
        const result = await client.SaveUrunAsync({
            UyeKodu: auth.UyeKodu,
            Urun: urunKart
        });
        */

        // Simulate Success for now as we don't have real creds
        console.log("Pushing to Ticimax:", JSON.stringify(urunKart, null, 2));

        // Store result
        const ticimaxId = "TEMP-" + Math.floor(Math.random() * 10000); // result.UrunID

        await docRef.update({
            status: "pushed",
            ticimaxProductId: ticimaxId,
            pushedAt: new Date(),
            lastError: null
        });

        return NextResponse.json({ success: true, ticimaxId });
    } catch (error: any) {
        console.error("Ticimax Push Error:", error);
        // Log error to Firestore
        const { productId } = await req.json().catch(() => ({ productId: "unknown" }));
        if (productId !== "unknown") {
            await adminDb.collection("products").doc(productId).update({
                lastError: error.message,
                status: "error"
            });
        }

        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}

"use client";

import { useState, useCallback, useEffect } from "react";
import { useDropzone } from "react-dropzone";
import { motion, AnimatePresence } from "framer-motion";
import { Upload, X, Sparkles, ArrowRight, Save, Wand2, Eye, Plus, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { cn } from "@/lib/utils";

interface ProductFormProps {
    attributes: any[];
}

interface Category {
    id: string;
    name: string;
}

export function ProductForm({ attributes }: ProductFormProps) {
    const [files, setFiles] = useState<File[]>([]);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState(""); // HTML or text
    const [aiLoading, setAiLoading] = useState(false);
    const [sku, setSku] = useState("");
    const [barcode, setBarcode] = useState("");
    const [categories, setCategories] = useState<Category[]>([]);
    const [selectedCategoryId, setSelectedCategoryId] = useState("");

    // Variants / Assortment state
    const [variants, setVariants] = useState<{ size: string, qty: number }[]>([
        { size: "", qty: 1 }
    ]);

    // Selected Attributes state
    const [selectedAttributes, setSelectedAttributes] = useState<Record<string, string>>({});

    // Review Modal state
    const [showReview, setShowReview] = useState(false);
    const [pushStatus, setPushStatus] = useState<"idle" | "pushing" | "success" | "error">("idle");
    const [pushResult, setPushResult] = useState<any>(null);

    useEffect(() => {
        // Fetch categories on mount
        fetch("/api/ticimax/category")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setCategories(data.categories);
                }
            })
            .catch(err => console.error("Failed to load categories", err));
    }, []);

    const onDrop = useCallback((acceptedFiles: File[]) => {
        setFiles((prev) => [...prev, ...acceptedFiles]);
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: {
            "image/*": [".jpeg", ".jpg", ".png", ".webp"],
        },
    });

    const removeFile = (index: number) => {
        setFiles((prev) => prev.filter((_, i) => i !== index));
    };

    // Variants handlers
    const addVariant = () => setVariants([...variants, { size: "", qty: 1 }]);
    const removeVariant = (index: number) => setVariants(variants.filter((_, i) => i !== index));
    const updateVariant = (index: number, field: "size" | "qty", value: string | number) => {
        const newVariants = [...variants];
        if (field === "qty") newVariants[index].qty = Number(value);
        else newVariants[index].size = String(value);
        setVariants(newVariants);
    };

    const handleAiFill = async () => {
        setAiLoading(true);
        try {
            // Mocking AI or calling real API if configured
            // For demo, we just fill some distinct values
            setTimeout(() => {
                const generatedDesc = `<p><strong>${title || "Ürün"}</strong> harika bir seçim.</p><ul><li>Kaliteli Malzeme</li><li>Rahat Kalıp</li></ul>`;
                setDescription(generatedDesc);
                setSku("RV-202602-" + Math.floor(Math.random() * 1000));

                // Mock selecting a category intelligently
                if (categories.length > 0) {
                    // Pick "Kadın Sandalet" if available, else random
                    const sandal = categories.find(c => c.name.toLowerCase().includes("sandalet"));
                    setSelectedCategoryId(sandal ? sandal.id : categories[0].id);
                }

                // Mock selecting first available value for first attribute
                if (attributes.length > 0 && attributes[0].values.length > 0) {
                    setSelectedAttributes(prev => ({ ...prev, [attributes[0].featureId]: attributes[0].values[0].valueId }));
                }
                setAiLoading(false);
            }, 1500);
        } catch (e) {
            console.error(e);
            setAiLoading(false);
        }
    };

    const preparePayload = () => {
        const categoryName = categories.find(c => c.id === selectedCategoryId)?.name || "";
        return {
            title,
            sku,
            barcode,
            description,
            categoryId: selectedCategoryId,
            categoryName,
            variants,
            selectedAttributes: Object.entries(selectedAttributes).map(([k, v]) => {
                const attrName = attributes.find(a => a.featureId.toString() === k)?.name;
                const valName = attributes.find(a => a.featureId.toString() === k)?.values.find((val: any) => val.valueId.toString() === v)?.name;
                return { featureId: k, valueId: v, name: attrName, valueName: valName };
            }),
            images: files.map(f => f.name),
            // Just for review visibility
            _meta: {
                imageCount: files.length,
                totalStock: variants.reduce((acc, curr) => acc + curr.qty, 0)
            }
        };
    };

    const handlePush = async () => {
        setPushStatus("pushing");
        try {
            const payload = preparePayload();
            // Here we would upload files first, then call push API
            // For now simulating API call
            const res = await fetch("/api/ticimax/push", {
                method: "POST",
                body: JSON.stringify({ productId: "DEMO_ID", ...payload })
            });
            const data = await res.json();
            setPushResult(data);
            setPushStatus(data.success ? "success" : "error");
        } catch (e: any) {
            setPushStatus("error");
            setPushResult({ error: e.message });
        }
    };

    return (
        <div className="space-y-8 max-w-5xl mx-auto p-6 relative">
            {/* Review Modal Overlay */}
            {showReview && (
                <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm">
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95 }}
                        animate={{ opacity: 1, scale: 1 }}
                        className="bg-background border rounded-xl shadow-2xl w-full max-w-3xl max-h-[90vh] flex flex-col"
                    >
                        <div className="p-6 border-b flex justify-between items-center">
                            <h2 className="text-2xl font-bold">Veri Gönderim Önizlemesi</h2>
                            <Button variant="ghost" size="icon" onClick={() => setShowReview(false)}>
                                <X className="w-5 h-5" />
                            </Button>
                        </div>
                        <div className="p-6 overflow-y-auto flex-1 space-y-4">
                            <div className="bg-muted p-4 rounded-lg font-mono text-xs overflow-x-auto">
                                <pre>{JSON.stringify(preparePayload(), null, 2)}</pre>
                            </div>
                        </div>
                        <div className="p-6 border-t flex justify-end gap-3 bg-muted/20">
                            <Button variant="outline" onClick={() => setShowReview(false)}>Düzenle</Button>
                            <Button
                                variant="premium"
                                onClick={handlePush}
                                disabled={pushStatus === "pushing" || pushStatus === "success"}
                            >
                                {pushStatus === "pushing" ? "Gönderiliyor..." : pushStatus === "success" ? "Başarıyla Gönderildi!" : "Onayla & Gönder"}
                            </Button>
                        </div>
                    </motion.div>
                </div>
            )}

            <div className="flex justify-between items-center">
                <div>
                    <h1 className="text-4xl font-bold tracking-tight bg-gradient-to-r from-gray-900 to-gray-600 dark:from-white dark:to-gray-400 bg-clip-text text-transparent">
                        Yeni Ürün
                    </h1>
                    <p className="text-muted-foreground mt-2">
                        Fotoğrafları yükleyin, asortiyi yönetin ve Ticimax'a gönderin.
                    </p>
                </div>
                <div className="flex gap-3">
                    <Button variant="outline">Taslağı Kaydet</Button>
                    <Button variant="premium" className="gap-2" onClick={() => setShowReview(true)}>
                        İncele & Gönder <Eye className="w-4 h-4" />
                    </Button>
                </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Left Column: Images & Basic Info */}
                <div className="lg:col-span-2 space-y-6">
                    <Card className="border-dashed border-2 border-primary/20 bg-background/50">
                        <CardContent className="pt-6">
                            <div
                                {...getRootProps()}
                                className={cn(
                                    "flex flex-col items-center justify-center h-48 rounded-xl transition-all cursor-pointer",
                                    isDragActive
                                        ? "bg-primary/5 scale-[0.99] border-primary"
                                        : "hover:bg-primary/5"
                                )}
                            >
                                <input {...getInputProps()} />
                                <div className="p-4 bg-primary/10 rounded-full mb-4">
                                    <Upload className="w-8 h-8 text-primary" />
                                </div>
                                <p className="text-lg font-medium">
                                    {isDragActive ? "Dosyaları buraya bırakın" : "Fotoğrafları sürükleyip bırakın"}
                                </p>
                            </div>

                            {/* Image Preview Grid */}
                            <AnimatePresence>
                                {files.length > 0 && (
                                    <div className="grid grid-cols-5 gap-3 mt-6">
                                        {files.map((file, i) => (
                                            <motion.div
                                                key={i}
                                                initial={{ opacity: 0, scale: 0.8 }}
                                                animate={{ opacity: 1, scale: 1 }}
                                                exit={{ opacity: 0, scale: 0.8 }}
                                                className="relative aspect-[3/4] group rounded-lg overflow-hidden border bg-background"
                                            >
                                                <img
                                                    src={URL.createObjectURL(file)}
                                                    alt="preview"
                                                    className="object-cover w-full h-full"
                                                />
                                                <button
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        removeFile(i);
                                                    }}
                                                    className="absolute top-1 right-1 p-1 bg-black/50 text-white rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                                                >
                                                    <X className="w-3 h-3" />
                                                </button>
                                            </motion.div>
                                        ))}
                                    </div>
                                )}
                            </AnimatePresence>
                        </CardContent>
                    </Card>

                    <Card>
                        <CardHeader>
                            <CardTitle>Ürün Detayları</CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Ürün Adı</label>
                                <div className="flex gap-2">
                                    <Input
                                        placeholder="Ürün adını girin..."
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                        className="flex-1"
                                    />
                                    <Button
                                        onClick={handleAiFill}
                                        disabled={aiLoading}
                                        className="bg-purple-600 hover:bg-purple-700 text-white gap-2"
                                    >
                                        {aiLoading ? (
                                            <Wand2 className="w-4 h-4 animate-spin" />
                                        ) : (
                                            <Sparkles className="w-4 h-4" />
                                        )}
                                        AI Doldur
                                    </Button>
                                </div>
                            </div>

                            <div className="grid grid-cols-2 gap-4">
                                <div className="space-y-2">
                                    <label className="text-sm font-medium">STOK KODU (SKU)</label>
                                    <div className="flex gap-2">
                                        <Input
                                            placeholder="Oluşturuluyor..."
                                            value={sku}
                                            readOnly
                                        />
                                        <Button variant="outline" size="icon" onClick={() => setSku("RV-" + Date.now())}>
                                            <Wand2 className="w-4 h-4" />
                                        </Button>
                                    </div>
                                </div>
                                <div className="space-y-2">
                                    <label className="text-sm font-medium">Barkod</label>
                                    <Input
                                        placeholder="EAN / Barkod"
                                        value={barcode}
                                        onChange={(e) => setBarcode(e.target.value)}
                                    />
                                </div>
                            </div>

                            <div className="space-y-2">
                                <label className="text-sm font-medium">Açıklama (HTML)</label>
                                <textarea
                                    className="flex min-h-[100px] w-full rounded-md border border-input bg-background/50 px-3 py-2 text-sm glass focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring"
                                    placeholder="Ürün açıklaması..."
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)}
                                />
                            </div>
                        </CardContent>
                    </Card>

                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between">
                            <CardTitle>Asorti / Varyantlar</CardTitle>
                            <Button size="sm" variant="secondary" onClick={addVariant}><Plus className="w-4 h-4 mr-2" /> Beden Ekle</Button>
                        </CardHeader>
                        <CardContent className="space-y-3">
                            {variants.map((v, i) => (
                                <div key={i} className="flex gap-4 items-center">
                                    <Input
                                        placeholder="Beden (Örn: 42)"
                                        value={v.size}
                                        onChange={(e) => updateVariant(i, "size", e.target.value)}
                                        className="w-32"
                                    />
                                    <Input
                                        type="number"
                                        placeholder="Adet"
                                        value={v.qty}
                                        onChange={(e) => updateVariant(i, "qty", e.target.value)}
                                        className="w-24"
                                    />
                                    <Button variant="ghost" size="icon" className="text-destructive" onClick={() => removeVariant(i)}>
                                        <Trash2 className="w-4 h-4" />
                                    </Button>
                                </div>
                            ))}
                        </CardContent>
                    </Card>
                </div>

                {/* Right Column: Settings & Attributes */}
                <div className="space-y-6">
                    <Card>
                        <CardHeader>
                            <CardTitle>Kategori & Durum</CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Kategori</label>
                                <select
                                    className="w-full text-sm p-2 rounded-md border bg-background/50 h-10"
                                    value={selectedCategoryId}
                                    onChange={(e) => setSelectedCategoryId(e.target.value)}
                                >
                                    <option value="">Kategori Seçiniz</option>
                                    {categories.map((c) => (
                                        <option key={c.id} value={c.id}>{c.name}</option>
                                    ))}
                                </select>
                            </div>

                            <div className="p-3 bg-muted/50 rounded-lg text-sm space-y-2">
                                <div className="flex justify-between">
                                    <span>Durum</span>
                                    <span className="font-medium text-amber-600">Taslak</span>
                                </div>
                                <div className="flex justify-between">
                                    <span>Görseller</span>
                                    <span className="font-medium">{files.length} Seçildi</span>
                                </div>
                                <div className="flex justify-between">
                                    <span>Toplam Stok</span>
                                    <span className="font-medium">{variants.reduce((a, b) => a + b.qty, 0)}</span>
                                </div>
                            </div>
                        </CardContent>
                    </Card>

                    <Card>
                        <CardHeader>
                            <CardTitle>Teknik Özellikler</CardTitle>
                            <CardDescription>Ticimax için özellikleri seçin</CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-4 max-h-[500px] overflow-y-auto pr-2">
                            <div className="space-y-3">
                                {attributes.length === 0 && <p className="text-sm text-yellow-500">Özellik bulunamadı. Lütfen aktarımı çalıştırın.</p>}
                                {attributes.slice(0, 15).map(attr => (
                                    <div key={attr.featureId} className="space-y-1">
                                        <label className="text-xs font-semibold">{attr.name}</label>
                                        <select
                                            className="w-full text-sm p-2 rounded-md border bg-background/50"
                                            value={selectedAttributes[attr.featureId] || ""}
                                            onChange={(e) => setSelectedAttributes({ ...selectedAttributes, [attr.featureId]: e.target.value })}
                                        >
                                            <option value="">Seçiniz...</option>
                                            {attr.values.map((v: any) => (
                                                <option key={v.valueId} value={v.valueId}>{v.name}</option>
                                            ))}
                                        </select>
                                    </div>
                                ))}
                                {attributes.length > 15 && <p className="text-xs text-muted-foreground">+ {attributes.length - 15} özellik daha...</p>}
                            </div>
                        </CardContent>
                    </Card>
                </div>
            </div>
        </div>
    );
}

